@import url('https://fonts.googleapis.com/css2?family=Tajawal:wght@200;300;400;500;700;800;900&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  * {
    font-family: 'Tajawal', sans-serif;
  }

  html {
    direction: rtl;
  }

  body {
    background: #fff;
    color: #1e293b;
    font-weight: 500;
    min-height: 100vh;
  }

  /* Smooth scrolling */
  html {
    scroll-behavior: smooth;
  }
  :root {
    /* Main Color Palette - Based on #7EE7FC */
    --primary: 195 100% 75%; /* #7EE7FC - Main brand color */
    --primary-dark: 195 100% 65%; /* #5DD3F0 - Darker shade */
    --primary-darker: 195 100% 55%; /* #3BC4E8 - Even darker */
    --primary-light: 195 100% 85%; /* #9FEDFC - Lighter shade */
    --primary-lighter: 195 100% 95%; /* #E6F9FD - Very light */

    /* Neutral Colors */
    --background: 0 0% 100%; /* White */
    --foreground: 210 11% 15%; /* Dark gray */
    --card: 0 0% 100%;
    --card-foreground: 210 11% 15%;
    --popover: 0 0% 100%;
    --popover-foreground: 210 11% 15%;
    --primary-foreground: 0 0% 98%;
    --secondary: 195 20% 96%; /* Very light blue-gray */
    --secondary-foreground: 210 11% 15%;
    --muted: 195 20% 96%;
    --muted-foreground: 210 11% 45%;
    --accent: 195 20% 96%;
    --accent-foreground: 210 11% 15%;

    /* Status Colors - Harmonious with main color */
    --success: 195 100% 40%; /* Darker blue-green for success */
    --warning: 45 100% 60%; /* Warm yellow that complements blue */
    --destructive: 0 70% 60%; /* Red for errors */
    --destructive-foreground: 0 0% 98%;

    /* UI Colors */
    --border: 195 20% 90%;
    --input: 195 20% 90%;
    --ring: 195 100% 75%;

    /* Chart Colors - All variations of main color */
    --chart-1: 195 100% 75%; /* Main blue */
    --chart-2: 195 100% 65%; /* Darker blue */
    --chart-3: 195 100% 55%; /* Even darker blue */
    --chart-4: 195 100% 85%; /* Lighter blue */
    --chart-5: 195 100% 95%; /* Very light blue */

    --radius: 0.5rem;
  }
  .dark {
    /* Dark mode - Same color palette but adjusted for dark theme */
    --background: 210 11% 8%; /* Dark blue-gray */
    --foreground: 0 0% 98%;
    --card: 210 11% 10%;
    --card-foreground: 0 0% 98%;
    --popover: 210 11% 10%;
    --popover-foreground: 0 0% 98%;
    --primary: 195 100% 75%; /* Keep main color bright in dark mode */
    --primary-foreground: 210 11% 8%;
    --secondary: 210 11% 15%;
    --secondary-foreground: 0 0% 98%;
    --muted: 210 11% 15%;
    --muted-foreground: 0 0% 70%;
    --accent: 210 11% 15%;
    --accent-foreground: 0 0% 98%;
    --destructive: 0 70% 60%;
    --destructive-foreground: 0 0% 98%;
    --border: 210 11% 20%;
    --input: 210 11% 20%;
    --ring: 195 100% 75%;
    --chart-1: 195 100% 75%;
    --chart-2: 195 100% 65%;
    --chart-3: 195 100% 55%;
    --chart-4: 195 100% 85%;
    --chart-5: 195 100% 95%;
  }
  .theme {
    --animate-orbit: orbit calc(var(--duration)*1s) linear infinite;
  }
}

