    background: linear-gradient(135deg, rgba(126, 231, 252, 0.1) 0%, rgba(93, 211, 240, 0.1) 50%, rgba(59, 196, 232, 0.1) 100%);
  }

  /* Feature Card */
  .feature-card {
    @apply bg-white/80 backdrop-blur-sm rounded-2xl p-8 shadow-lg hover:shadow-2xl transition-all duration-500 border border-slate-200/50 text-center cursor-pointer;
  }

  .feature-card:hover {
    @apply transform -translate-y-2 scale-105;
  }

  .feature-icon {
    @apply w-20 h-20 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-lg transition-all duration-500;
  }

  /* Pricing Card */
  .pricing-card {
    @apply bg-gradient-to-br from-white/95 to-slate-50/95 backdrop-blur-sm rounded-3xl p-8 shadow-xl hover:shadow-2xl transition-all duration-500 border border-slate-200/50 relative overflow-hidden;
  }

  .pricing-card:before {
    content: '';
    @apply absolute -top-1/2 -right-1/2 w-full h-full bg-gradient-to-br from-blue-200/20 via-purple-200/20 to-teal-200/20 rotate-45 opacity-0 hover:opacity-100 transition-opacity duration-500;
  }

  /* Floating Animation */
  .float-animation {
    animation: float 3s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  /* Pulse Animation */
  .pulse-soft {
    animation: pulse-soft 2s ease-in-out infinite;
  }

  @keyframes pulse-soft {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.7; }
  }

  /* Slide Up Animation */
  .slide-up {
    animation: slideUp 0.6s ease-out;
  }

  @keyframes slideUp {
    from { 
      opacity: 0; 
      transform: translateY(30px); 
    }
    to { 
      opacity: 1; 
      transform: translateY(0); 
    }
  }

  /* Scale In Animation */
  .scale-in {
    animation: scaleIn 0.4s ease-out;
  }

  @keyframes scaleIn {
    from { 
      opacity: 0; 
      transform: scale(0.9); 
    }
    to { 
      opacity: 1; 
      transform: scale(1); 
    }
  }

  /* Interactive Button Effects */
  .btn-interactive {
    @apply relative overflow-hidden;
  }

  .btn-interactive:after {
    content: '';
    @apply absolute inset-0 bg-gradient-to-r from-white/0 via-white/40 to-white/0 -skew-x-12 -translate-x-full transition-transform duration-700;
  }

  .btn-interactive:hover:after {
    @apply translate-x-full;
  }

  /* Glow Effect */
  .glow-soft {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.4);
  }

  .glow-success {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.4);
  }

  .glow-warning {
    box-shadow: 0 0 20px hsl(var(--warning) / 0.3);
  }
}

@layer utilities {
  .animate-fade-in {
    animation: fade-in 0.8s ease-in-out;
  }

  .animate-slide-up {
    animation: slide-up 0.6s ease-out;
  }

  .animate-bounce-gentle {
    animation: bounce-gentle 1s ease-in-out;
  }

  @keyframes fade-in {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes slide-up {
    from { 
      opacity: 0; 
      transform: translateY(30px); 
    }
    to { 
      opacity: 1; 
      transform: translateY(0); 
    }
  }

  @keyframes bounce-gentle {
    0%, 100% { transform: translateY(0); }
    25% { transform: translateY(-5px); }
    50% { transform: translateY(-8px); }
    75% { transform: translateY(-3px); }
  }
}

/* Custom Scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: linear-gradient(135deg, hsl(var(--secondary)), hsl(var(--muted)));
  border-radius: 10px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--primary-dark)));
  border-radius: 10px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(135deg, hsl(var(--primary-dark)), hsl(var(--primary-darker)));
}

/* Selection */
::selection {
  background: linear-gradient(135deg, hsl(var(--primary) / 0.3), hsl(var(--primary-dark) / 0.3));
  color: hsl(var(--foreground));
}

/* Loading Animation */
.loading-dots {
  display: inline-flex;
  gap: 4px;
}

.loading-dots span {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: currentColor;
  animation: loading 1.4s ease-in-out infinite both;
}

.loading-dots span:nth-child(1) { animation-delay: -0.32s; }
.loading-dots span:nth-child(2) { animation-delay: -0.16s; }

@keyframes loading {
  0%, 80%, 100% {
    transform: scale(0.8);
    opacity: 0.5;
  }
  40% {
    transform: scale(1);
    opacity: 1;
  }
}

/* Backdrop Blur Support */
@supports (backdrop-filter: blur()) {
  .backdrop-blur-glass {
    backdrop-filter: blur(10px);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}

@theme inline {
  @keyframes orbit {
  0% {
    transform: rotate(0deg) translateY(calc(var(--radius) * 1px)) rotate(0deg);
    }
  100% {
    transform: rotate(360deg) translateY(calc(var(--radius) * 1px)) rotate(-360deg);
    }
  }
}

