    color: '#8B5CF6' // Purple
  },
  logo4: {
    src: '/logo4.png',
    color: '#F59E0B' // Amber
  }
};

// --- Memoized Logo Component ---
const LogoComponent = memo(({ type }: LogoProps) => {
  const logoData = logoComponents[type];
  return logoData ? (
    <img 
      src={logoData.src} 
      alt={`${type} logo`}
      className="w-full h-full object-contain rounded-3xl"
