});
OrbitingSkill.displayName = 'OrbitingSkill';

// --- Optimized Orbit Path Component ---
const GlowingOrbitPath = memo(({ radius, glowColor = 'blue', animationDelay = 0 }: GlowingOrbitPathProps) => {
  const glowColors = {
    blue: {
      primary: 'rgba(59, 130, 246, 0.4)',
